package com.fourtwo.hookintent.data;

import android.annotation.SuppressLint;
import android.content.ClipData;
import android.content.ClipboardManager;
import android.content.Context;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.graphics.Canvas;
import android.graphics.drawable.BitmapDrawable;
import android.graphics.drawable.Drawable;
import android.graphics.drawable.VectorDrawable;
import android.os.Build;

import java.io.ByteArrayOutputStream;
import java.util.Base64;

public class ImagesBase64 {

    public static String r_drawable_ic_launcher_round = "";
    public static String r_drawable_open = "";
    public static String r_drawable_close = "";

    public static String r_drawable_delete = "";

    public static String fixBase64String(String base64String) {
        int paddingCount = 4 - (base64String.length() % 4);
        if (paddingCount < 4) {
            base64String += "=".repeat(paddingCount); // 手动补全 '='
        }
        return base64String;
    }

    public static Drawable base64ToDrawable(Context context, String base64String) {
        byte[] decodedBytes = new byte[0];
        if (android.os.Build.VERSION.SDK_INT >= android.os.Build.VERSION_CODES.O) {
            base64String = fixBase64String(base64String);
            decodedBytes = Base64.getDecoder().decode(base64String);
        }
        Bitmap bitmap = BitmapFactory.decodeByteArray(decodedBytes, 0, decodedBytes.length);

        return new BitmapDrawable(context.getResources(), bitmap);
    }

    private static Bitmap vectorDrawableToBitmap(VectorDrawable vectorDrawable) {
        // 创建一个空白的 Bitmap
        Bitmap bitmap = Bitmap.createBitmap(
                vectorDrawable.getIntrinsicWidth(),
                vectorDrawable.getIntrinsicHeight(),
                Bitmap.Config.ARGB_8888
        );

        // 创建一个 Canvas 并将 VectorDrawable 绘制到 Canvas 上
        Canvas canvas = new Canvas(bitmap);
        vectorDrawable.setBounds(0, 0, canvas.getWidth(), canvas.getHeight());
        vectorDrawable.draw(canvas);

        return bitmap;
    }
    public static String drawableToBase64(Context context, int drawableId) {
        @SuppressLint("UseCompatLoadingForDrawables") Drawable drawable = context.getResources().getDrawable(drawableId, context.getTheme());
        Bitmap bitmap;
        if (drawable instanceof BitmapDrawable) {
            // 如果是 BitmapDrawable，直接获取 Bitmap
            bitmap = ((BitmapDrawable) drawable).getBitmap();
        } else if (drawable instanceof VectorDrawable) {
            // 如果是 VectorDrawable，将其渲染为 Bitmap
            bitmap = vectorDrawableToBitmap((VectorDrawable) drawable);
        } else {
            throw new IllegalArgumentException("不支持的 Drawable 类型");
        }

        ByteArrayOutputStream outputStream = new ByteArrayOutputStream();
        bitmap.compress(Bitmap.CompressFormat.PNG, 100, outputStream);
        byte[] byteArray = outputStream.toByteArray();

        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            String base64String = Base64.getEncoder().encodeToString(byteArray);
            ClipboardManager clipboard = (ClipboardManager) context.getSystemService(Context.CLIPBOARD_SERVICE);
            ClipData clip = ClipData.newPlainText("Base64 Image", base64String);
            clipboard.setPrimaryClip(clip);
            return base64String;
        }
        return null;
    }

}
